				this.pkid = entity.pkid;
			}

			#endregion
		}
		
		/// <summary>
		/// Initializes a new instance of the FieldKey class.
		/// </summary>
		public FieldKey(System.Int32 _pkid)
		{
			#region Init Properties

			this.pkid = _pkid;

			#endregion
		}
		
		#endregion Constructors

		#region Properties
		
		// member variable for the Entity property
		private FieldBase _entity;
		
		/// <summary>
		/// Gets or sets the Entity property.
		/// </summary>
		public FieldBase Entity
		{
			get { return _entity; }
			set { _entity = value; }
		}
		
		// member variable for the pkid property
		private System.Int32 _pkid;
		
		/// <summary>
		/// Gets or sets the pkid property.
		/// </summary>
		public System.Int32 pkid
		{
			get { return _pkid; }
			set
			{
				if ( this.Entity != null )
					this.Entity.pkid = value;
				
				_pkid = value;
			}
		}
		
		#endregion

		#region Methods
		
		/// <summary>
		/// Reads values from the supplied <see cref="IDictionary"/> object into
