		/// <summary>
		/// Lets you efficiently bulk insert many entities to the database.
		/// </summary>
		/// <param name="transactionManager">The transaction manager.</param>
		/// <param name="entities">The entities.</param>
		/// <remarks>
		///		After inserting into the datasource, the MetaBuilder.BusinessLogic.Artifact object will be updated
		/// 	to refelect any changes made by the datasource. (ie: identity or computed columns)
		/// </remarks>	
		public override void BulkInsert(TransactionManager transactionManager, TList<MetaBuilder.BusinessLogic.Artifact> entities)
		{
			//System.Data.SqlClient.SqlBulkCopy bulkCopy = new System.Data.SqlClient.SqlBulkCopy(this._connectionString, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints); //, null);
			
			System.Data.SqlClient.SqlBulkCopy bulkCopy = null;
	
			if (transactionManager != null && transactionManager.IsOpen)
			{			
				System.Data.SqlClient.SqlConnection cnx = transactionManager.TransactionObject.Connection as System.Data.SqlClient.SqlConnection;
				System.Data.SqlClient.SqlTransaction transaction = transactionManager.TransactionObject as System.Data.SqlClient.SqlTransaction;
				bulkCopy = new System.Data.SqlClient.SqlBulkCopy(cnx, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints, transaction); //, null);
			}
			else
			{
				bulkCopy = new System.Data.SqlClient.SqlBulkCopy(this._connectionString, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints); //, null);
			}
			
			bulkCopy.BulkCopyTimeout = 360;
			bulkCopy.DestinationTableName = "Artifact";
			
			DataTable dataTable = new DataTable();
			DataColumn col0 = dataTable.Columns.Add("ArtifactID", typeof(System.Int32));
			col0.AllowDBNull = false;		
			DataColumn col1 = dataTable.Columns.Add("CAid", typeof(System.Int32));
			col1.AllowDBNull = false;		
			DataColumn col2 = dataTable.Columns.Add("ObjectID", typeof(System.Int32));
			col2.AllowDBNull = false;		
			DataColumn col3 = dataTable.Columns.Add("ChildObjectID", typeof(System.Int32));
			col3.AllowDBNull = false;		
			DataColumn col4 = dataTable.Columns.Add("ArtifactObjectID", typeof(System.Int32));
			col4.AllowDBNull = false;		
			DataColumn col5 = dataTable.Columns.Add("ObjectMachine", typeof(System.String));
			col5.AllowDBNull = false;		
			DataColumn col6 = dataTable.Columns.Add("ChildObjectMachine", typeof(System.String));
			col6.AllowDBNull = false;		
			DataColumn col7 = dataTable.Columns.Add("ArtefactMachine", typeof(System.String));
			col7.AllowDBNull = false;		
			
			bulkCopy.ColumnMappings.Add("ArtifactID", "ArtifactID");
			bulkCopy.ColumnMappings.Add("CAid", "CAid");
			bulkCopy.ColumnMappings.Add("ObjectID", "ObjectID");
			bulkCopy.ColumnMappings.Add("ChildObjectID", "ChildObjectID");
			bulkCopy.ColumnMappings.Add("ArtifactObjectID", "ArtifactObjectID");
			bulkCopy.ColumnMappings.Add("ObjectMachine", "ObjectMachine");
			bulkCopy.ColumnMappings.Add("ChildObjectMachine", "ChildObjectMachine");
			bulkCopy.ColumnMappings.Add("ArtefactMachine", "ArtefactMachine");
			
			foreach(MetaBuilder.BusinessLogic.Artifact entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
					
				DataRow row = dataTable.NewRow();
				
					row["ArtifactID"] = entity.ArtifactID;
							
				
					row["CAid"] = entity.CAid;
							
				
					row["ObjectID"] = entity.ObjectID;
							
				
					row["ChildObjectID"] = entity.ChildObjectID;
							
				
					row["ArtifactObjectID"] = entity.ArtifactObjectID;
							
				
					row["ObjectMachine"] = entity.ObjectMachine;
							
				
					row["ChildObjectMachine"] = entity.ChildObjectMachine;
							
				
					row["ArtefactMachine"] = entity.ArtefactMachine;
							
				
				dataTable.Rows.Add(row);
			}		
			
			// send the data to the server		
			bulkCopy.WriteToServer(dataTable);		
			
			// update back the state
			foreach(MetaBuilder.BusinessLogic.Artifact entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
			
				entity.AcceptChanges();
			}
		}
				
