		public override void BulkInsert(TransactionManager transactionManager, TList<MetaBuilder.BusinessLogic.MetaObject> entities)
		{
			//System.Data.SqlClient.SqlBulkCopy bulkCopy = new System.Data.SqlClient.SqlBulkCopy(this._connectionString, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints); //, null);
			
			System.Data.SqlClient.SqlBulkCopy bulkCopy = null;
	
			if (transactionManager != null && transactionManager.IsOpen)
			{			
				System.Data.SqlClient.SqlConnection cnx = transactionManager.TransactionObject.Connection as System.Data.SqlClient.SqlConnection;
				System.Data.SqlClient.SqlTransaction transaction = transactionManager.TransactionObject as System.Data.SqlClient.SqlTransaction;
				bulkCopy = new System.Data.SqlClient.SqlBulkCopy(cnx, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints, transaction); //, null);
			}
			else
			{
				bulkCopy = new System.Data.SqlClient.SqlBulkCopy(this._connectionString, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints); //, null);
			}
			
			bulkCopy.BulkCopyTimeout = 360;
			bulkCopy.DestinationTableName = "MetaObject";
			
			DataTable dataTable = new DataTable();
			DataColumn col0 = dataTable.Columns.Add("pkid", typeof(System.Int32));
			col0.AllowDBNull = false;		
			DataColumn col1 = dataTable.Columns.Add("Class", typeof(System.String));
			col1.AllowDBNull = false;		
			DataColumn col2 = dataTable.Columns.Add("WorkspaceName", typeof(System.String));
			col2.AllowDBNull = false;		
			DataColumn col3 = dataTable.Columns.Add("UserID", typeof(System.Int32));
			col3.AllowDBNull = false;		
			DataColumn col4 = dataTable.Columns.Add("Machine", typeof(System.String));
			col4.AllowDBNull = false;		
			DataColumn col5 = dataTable.Columns.Add("VCStatusID", typeof(System.Int32));
			col5.AllowDBNull = false;		
			DataColumn col6 = dataTable.Columns.Add("VCMachineID", typeof(System.String));
			col6.AllowDBNull = true;		
			DataColumn col7 = dataTable.Columns.Add("WorkspaceTypeId", typeof(System.Int32));
			col7.AllowDBNull = true;		
			DataColumn col8 = dataTable.Columns.Add("DateCreated", typeof(System.DateTime));
			col8.AllowDBNull = true;		
			DataColumn col9 = dataTable.Columns.Add("LastModified", typeof(System.DateTime));
			col9.AllowDBNull = true;		
			
			bulkCopy.ColumnMappings.Add("pkid", "pkid");
			bulkCopy.ColumnMappings.Add("Class", "Class");
			bulkCopy.ColumnMappings.Add("WorkspaceName", "WorkspaceName");
			bulkCopy.ColumnMappings.Add("UserID", "UserID");
			bulkCopy.ColumnMappings.Add("Machine", "Machine");
			bulkCopy.ColumnMappings.Add("VCStatusID", "VCStatusID");
			bulkCopy.ColumnMappings.Add("VCMachineID", "VCMachineID");
			bulkCopy.ColumnMappings.Add("WorkspaceTypeId", "WorkspaceTypeId");
			bulkCopy.ColumnMappings.Add("DateCreated", "DateCreated");
			bulkCopy.ColumnMappings.Add("LastModified", "LastModified");
			
			foreach(MetaBuilder.BusinessLogic.MetaObject entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
					
				DataRow row = dataTable.NewRow();
				
					row["pkid"] = entity.pkid;
							
				
					row["Class"] = entity.Class;
							
				
					row["WorkspaceName"] = entity.WorkspaceName;
							
				
					row["UserID"] = entity.UserID;
							
				
					row["Machine"] = entity.Machine;
							
				
					row["VCStatusID"] = entity.VCStatusID;
							
				
					row["VCMachineID"] = entity.VCMachineID;
							
				
					row["WorkspaceTypeId"] = entity.WorkspaceTypeId.HasValue ? (object) entity.WorkspaceTypeId  : System.DBNull.Value;
							
				
					row["DateCreated"] = entity.DateCreated.HasValue ? (object) entity.DateCreated  : System.DBNull.Value;
							
				
					row["LastModified"] = entity.LastModified.HasValue ? (object) entity.LastModified  : System.DBNull.Value;
							
				
				dataTable.Rows.Add(row);
			}		
			
			// send the data to the server		
			bulkCopy.WriteToServer(dataTable);		
			
			// update back the state
			foreach(MetaBuilder.BusinessLogic.MetaObject entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
			
				entity.AcceptChanges();
			}
		}
				
