				results = Utility.ExecuteNonQuery(transactionManager, commandWrapper);
			}
			else
			{
				results = Utility.ExecuteNonQuery(database,commandWrapper);
			}
					
			object _pkid = database.GetParameterValue(commandWrapper, "@pkid");
			entity.pkid = (System.Int32)_pkid;
			
			entity.OriginalMachine = entity.Machine;
			
			entity.AcceptChanges();
	
			//Provider Data Requested Command Event
			OnDataRequested(new CommandEventArgs(commandWrapper, "Insert", entity));

			return Convert.ToBoolean(results);
		}	
		#endregion

		#region Update Methods
				
		/// <summary>
		/// 	Update an existing row in the datasource.
		/// </summary>
		/// <param name="transactionManager"><see cref="TransactionManager"/> object</param>
		/// <param name="entity">MetaBuilder.BusinessLogic.MetaObject object to update.</param>
		/// <remarks>
		///		After updating the datasource, the MetaBuilder.BusinessLogic.MetaObject object will be updated
		/// 	to refelect any changes made by the datasource. (ie: identity or computed columns)
		/// </remarks>
		/// <returns>Returns true if operation is successful.</returns>
        /// <exception cref="System.Exception">The command could not be executed.</exception>
        /// <exception cref="System.Data.DataException">The <paramref name="transactionManager"/> is not open.</exception>
        /// <exception cref="System.Data.Common.DbException">The command could not be executed.</exception>
		public override bool Update(TransactionManager transactionManager, MetaBuilder.BusinessLogic.MetaObject entity)
		{
			SqlDatabase database = new SqlDatabase(this._connectionString);
			DbCommand commandWrapper = StoredProcedureProvider.GetCommandWrapper(database, "dbo.PROC_MetaObject_Update", _useStoredProcedure);
			
			database.AddInParameter(commandWrapper, "@pkid", DbType.Int32, entity.pkid );
			database.AddInParameter(commandWrapper, "@Class", DbType.AnsiString, entity.Class );
			database.AddInParameter(commandWrapper, "@WorkspaceName", DbType.AnsiString, entity.WorkspaceName );
			database.AddInParameter(commandWrapper, "@UserID", DbType.Int32, entity.UserID );
			database.AddInParameter(commandWrapper, "@Machine", DbType.AnsiString, entity.Machine );
			database.AddInParameter(commandWrapper, "@OriginalMachine", DbType.AnsiString, entity.OriginalMachine);
			database.AddInParameter(commandWrapper, "@VCStatusID", DbType.Int32, entity.VCStatusID );
			database.AddInParameter(commandWrapper, "@VCMachineID", DbType.AnsiString, entity.VCMachineID );
			database.AddInParameter(commandWrapper, "@WorkspaceTypeId", DbType.Int32, (entity.WorkspaceTypeId.HasValue ? (object) entity.WorkspaceTypeId : System.DBNull.Value) );
			database.AddInParameter(commandWrapper, "@DateCreated", DbType.DateTime, (entity.DateCreated.HasValue ? (object) entity.DateCreated : System.DBNull.Value) );
			database.AddInParameter(commandWrapper, "@LastModified", DbType.DateTime, (entity.LastModified.HasValue ? (object) entity.LastModified : System.DBNull.Value) );
			
			int results = 0;
			
			//Provider Data Requesting Command Event
			OnDataRequesting(new CommandEventArgs(commandWrapper, "Update", entity));

			if (transactionManager != null)
			{
				results = Utility.ExecuteNonQuery(transactionManager, commandWrapper);
			}
			else
			{
				results = Utility.ExecuteNonQuery(database,commandWrapper);
			}
			
			//Stop Tracking Now that it has been updated and persisted.
			if (DataRepository.Provider.EnableEntityTracking)
				EntityManager.StopTracking(entity.EntityTrackingKey);
			
			entity.OriginalMachine = entity.Machine;
			
			entity.AcceptChanges();
			
			//Provider Data Requested Command Event
			OnDataRequested(new CommandEventArgs(commandWrapper, "Update", entity));

			return Convert.ToBoolean(results);
		}
			
		#endregion
		
