		/// <summary>
		/// Lets you efficiently bulk insert many entities to the database.
		/// </summary>
		/// <param name="transactionManager">The transaction manager.</param>
		/// <param name="entities">The entities.</param>
		/// <remarks>
		///		After inserting into the datasource, the MetaBuilder.BusinessLogic.ObjectFieldValue object will be updated
		/// 	to refelect any changes made by the datasource. (ie: identity or computed columns)
		/// </remarks>	
		public override void BulkInsert(TransactionManager transactionManager, TList<MetaBuilder.BusinessLogic.ObjectFieldValue> entities)
		{
			//System.Data.SqlClient.SqlBulkCopy bulkCopy = new System.Data.SqlClient.SqlBulkCopy(this._connectionString, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints); //, null);
			
			System.Data.SqlClient.SqlBulkCopy bulkCopy = null;
	
			if (transactionManager != null && transactionManager.IsOpen)
			{			
				System.Data.SqlClient.SqlConnection cnx = transactionManager.TransactionObject.Connection as System.Data.SqlClient.SqlConnection;
				System.Data.SqlClient.SqlTransaction transaction = transactionManager.TransactionObject as System.Data.SqlClient.SqlTransaction;
				bulkCopy = new System.Data.SqlClient.SqlBulkCopy(cnx, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints, transaction); //, null);
			}
			else
			{
				bulkCopy = new System.Data.SqlClient.SqlBulkCopy(this._connectionString, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints); //, null);
			}
			
			bulkCopy.BulkCopyTimeout = 360;
			bulkCopy.DestinationTableName = "ObjectFieldValue";
			
			DataTable dataTable = new DataTable();
			DataColumn col0 = dataTable.Columns.Add("ObjectID", typeof(System.Int32));
			col0.AllowDBNull = false;		
			DataColumn col1 = dataTable.Columns.Add("FieldID", typeof(System.Int32));
			col1.AllowDBNull = false;		
			DataColumn col2 = dataTable.Columns.Add("ValueString", typeof(System.String));
			col2.AllowDBNull = true;		
			DataColumn col3 = dataTable.Columns.Add("ValueInt", typeof(System.Int32));
			col3.AllowDBNull = true;		
			DataColumn col4 = dataTable.Columns.Add("ValueDouble", typeof(System.Decimal));
			col4.AllowDBNull = true;		
			DataColumn col5 = dataTable.Columns.Add("ValueObjectID", typeof(System.Int32));
			col5.AllowDBNull = true;		
			DataColumn col6 = dataTable.Columns.Add("ValueDate", typeof(System.DateTime));
			col6.AllowDBNull = true;		
			DataColumn col7 = dataTable.Columns.Add("ValueBoolean", typeof(System.Boolean));
			col7.AllowDBNull = true;		
			DataColumn col8 = dataTable.Columns.Add("ValueLongText", typeof(System.String));
			col8.AllowDBNull = true;		
			DataColumn col9 = dataTable.Columns.Add("ValueRTF", typeof(System.String));
			col9.AllowDBNull = true;		
			DataColumn col10 = dataTable.Columns.Add("MachineID", typeof(System.String));
			col10.AllowDBNull = false;		
			
			bulkCopy.ColumnMappings.Add("ObjectID", "ObjectID");
			bulkCopy.ColumnMappings.Add("FieldID", "FieldID");
			bulkCopy.ColumnMappings.Add("ValueString", "ValueString");
			bulkCopy.ColumnMappings.Add("ValueInt", "ValueInt");
			bulkCopy.ColumnMappings.Add("ValueDouble", "ValueDouble");
			bulkCopy.ColumnMappings.Add("ValueObjectID", "ValueObjectID");
			bulkCopy.ColumnMappings.Add("ValueDate", "ValueDate");
			bulkCopy.ColumnMappings.Add("ValueBoolean", "ValueBoolean");
			bulkCopy.ColumnMappings.Add("ValueLongText", "ValueLongText");
			bulkCopy.ColumnMappings.Add("ValueRTF", "ValueRTF");
			bulkCopy.ColumnMappings.Add("MachineID", "MachineID");
			
			foreach(MetaBuilder.BusinessLogic.ObjectFieldValue entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
					
				DataRow row = dataTable.NewRow();
				
					row["ObjectID"] = entity.ObjectID;
							
				
					row["FieldID"] = entity.FieldID;
							
				
					row["ValueString"] = entity.ValueString;
							
				
					row["ValueInt"] = entity.ValueInt.HasValue ? (object) entity.ValueInt  : System.DBNull.Value;
							
				
					row["ValueDouble"] = entity.ValueDouble.HasValue ? (object) entity.ValueDouble  : System.DBNull.Value;
							
				
					row["ValueObjectID"] = entity.ValueObjectID.HasValue ? (object) entity.ValueObjectID  : System.DBNull.Value;
							
				
					row["ValueDate"] = entity.ValueDate.HasValue ? (object) entity.ValueDate  : System.DBNull.Value;
							
				
					row["ValueBoolean"] = entity.ValueBoolean.HasValue ? (object) entity.ValueBoolean  : System.DBNull.Value;
							
				
					row["ValueLongText"] = entity.ValueLongText;
							
				
					row["ValueRTF"] = entity.ValueRTF;
							
				
					row["MachineID"] = entity.MachineID;
							
				
				dataTable.Rows.Add(row);
			}		
			
			// send the data to the server		
			bulkCopy.WriteToServer(dataTable);		
			
			// update back the state
			foreach(MetaBuilder.BusinessLogic.ObjectFieldValue entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
			
				entity.AcceptChanges();
			}
		}
				
