INSERT INTO [dbo].[WorkspaceType]
	(
	[Description]
	)
VALUES
	(
	@Description
	)

-- Get the identity value
SET @pkid = SCOPE_IDENTITY()

BEGIN
-- Create a temp table to store the select results CREATE TABLE #PageIndex ( [IndexId] int IDENTITY (1, 1) NOT NULL, [pkid] int )
-- Insert into the temp table INSERT INTO #PageIndex ( [pkid] ) SELECT TOP {3} [pkid] FROM [dbo].[WorkspaceType] {0} ORDER BY {1}
-- Return paged results SELECT O.[pkid], O.[Description] FROM [dbo].[WorkspaceType] O, #PageIndex PageIndex WHERE PageIndex.IndexId > {2} AND O.[pkid] = PageIndex.[pkid] ORDER BY PageIndex.IndexId
-- get total count SELECT COUNT(*) AS TotalRowCount FROM [dbo].[WorkspaceType] {0};
DROP TABLE #PageIndex END


