			database.AddInParameter(commandWrapper, "@Machine", DbType.AnsiString, entity.Machine );
			database.AddInParameter(commandWrapper, "@OriginalMachine", DbType.AnsiString, entity.OriginalMachine);
			database.AddInParameter(commandWrapper, "@WorkspaceTypeId", DbType.Int32, entity.WorkspaceTypeId );
			database.AddInParameter(commandWrapper, "@OriginalFileUniqueID", DbType.Guid, entity.OriginalFileUniqueID );
			
			int results = 0;
			
			//Provider Data Requesting Command Event
			OnDataRequesting(new CommandEventArgs(commandWrapper, "Update", entity));

			if (transactionManager != null)
			{
				results = Utility.ExecuteNonQuery(transactionManager, commandWrapper);
			}
			else
			{
				results = Utility.ExecuteNonQuery(database,commandWrapper);
			}
			
			//Stop Tracking Now that it has been updated and persisted.
			if (DataRepository.Provider.EnableEntityTracking)
				EntityManager.StopTracking(entity.EntityTrackingKey);
			
			entity.OriginalMachine = entity.Machine;
			
			entity.AcceptChanges();
			
			//Provider Data Requested Command Event
			OnDataRequested(new CommandEventArgs(commandWrapper, "Update", entity));

			return Convert.ToBoolean(results);
		}
			
		#endregion
		
